			long t2 = System.currentTimeMillis();
			String duration = String.format("%.3f secs", (t2 -t1)/1000.0);
			msg = msg.replace("%DURATION%", duration);
			log.log(level, "tid="+Thread.currentThread().getId()+" oid="+opid+"  "+msg);
		}
	}
}

